const BOOL GAME_TTYPE::mfIsMissileBlocker (THINGTYPE const type)
{
// [d1-09-97 JPC] Added so fireballs won't blow up when they hit bushes.
// Use existing test (from AIFIREBL.CPP) and add another condition:
// must not be in a battle scene.

	SCENE_AI::STATE SceneState;
	SCENE_AI::SCENE_TYPE SceneType;
	SCENE * const pCurrentScene = (SCENE * const) BLKPTR(SCENE_MGR::hCurrentScene);
	pCurrentScene->mfGetSceneAIState(&SceneState, &SceneType);
	
	return ((type > LAST_GAME_AVATAR) &&
		!(type >= FIREBALL_1 && type <= LORES_CRUMBLE_1) &&
		(SceneType != SCENE_AI::BATTLE_SCENE));
		
}


